// For now positions are read from a CSV file, generated by the simulation Python script
// To-be-removed
function readValuesFromCSV(fileName, callback, worldWindow) {
    let xhttp = new XMLHttpRequest();
    xhttp.onreadystatechange = function() {
        if (this.readyState == 4 && this.status == 200) {
            const text = xhttp.responseText;

            let values = [];
            let lines = text.split('\n');
            lines.forEach((line) => {
                if (line.length == 0) {
                    return;
                }

                let lineValues = line.split(',');
                values.push(lineValues.map((v) => parseFloat(v)));
            });

            callback(worldWindow, values);
        }
    };
    xhttp.open("GET", fileName, true);
    xhttp.send();
}